mapper.select:
    	 		select 
						t1.id                             ,
						t1.role_code                      ,
						t1.mapping_role_code              ,
						t1.validate_state                 

				from jbpm4_role_mapping 	t1
				where 1=1
				and t1.validate_state='1'

						and t1.ID =#{dto.id}


						and t1.ROLE_CODE =#{dto.roleCode}


						and t1.MAPPING_ROLE_CODE =#{dto.mappingRoleCode}


						and t1.VALIDATE_STATE =#{dto.validateState}


				order by t1.id desc

select t1.id , t1.role_code , t1.mapping_role_code , t1.validate_state from jbpm4_role_mapping t1 where 1=1 and t1.validate_state='1'
and t1.ID = #{dto.id}
and t1.ROLE_CODE = #{dto.roleCode}
and t1.ROLE_CODE in (${dto.roleCodes})
and t1.MAPPING_ROLE_CODE = #{dto.mappingRoleCode}

mapper.insert:

	SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE  TABLE_NAME='jbpm4_role_mapping'

insert into jbpm4_role_mapping
(   		
		id                             ,
		role_code                      ,
		mapping_role_code              ,
		validate_state                 
)
values(#{dto.id}
		,#{dto.roleCode,jdbcType=VARCHAR}
		,#{dto.mappingRoleCode,jdbcType=VARCHAR}
		,'1'
)
