mapper.select:
    	 		select 
						t1.id                             ,
						t1.user_name                      ,
						t1.user_no                        ,
						t1.login_name                     ,
						t1.password                       ,
						t1.salt                           ,
						t1.mobile                         ,
						t1.email                          ,
						t1.user_image                     ,
						t1.sex                            ,
						t1.birthday                       ,
						t1.nationality                    ,
						t1.education                      ,
						t1.job                            ,
						t1.home_address                   ,
						t1.home_zipcode                   ,
						t1.home_tel                       ,
						t1.office_tel                     ,
						t1.office_address                 ,
						t1.order_by                       ,
						t1.validate_state                 ,
						t1.is_locked                      ,
						t1.version                        ,
						(SELECT B.POSITION_NAME FROM SYS_POSITION B WHERE B.ID=(
							SELECT A.POSITION_ID FROM SYS_ORG_USER A WHERE A.USER_ID=t1.id limit 1,1)) AS POSITION_CN

				from sys_user 	t1 ,vmuser_vmorg_map t2 
				where t1.validate_state='1' and t1.id=t2.user_id 


					 	 and exists (select t2.id from vmuser_vmorg_map t2 where  t2.org_id =#{orgId} and  t2.user_id = t1.id)


				and exists (select t2.id from vmuser_vmorg_map t2 where  1=1 and t2.user_id = t1.id
				and t2.org_id in (

						    	select a.org_id from (select *from vmtree_info where org_type=#{orgType} ) a



						    ${orgIds}

						)
				)


					 	and exists (select t2.id from vmuser_vmorg_map t2 where  t2.org_type=#{orgType} and t2.user_id = t1.id) and t2.org_type=#{orgType} 


						and t1.ID =#{dto.id}


						and t1.USER_NAME like concat('%',#{dto.userName},'%')


						and t1.USER_NO  like concat('%',#{dto.userNo},'%')


						and t1.LOGIN_NAME like concat('%',#{dto.loginName},'%')


						and t1.PASSWORD =#{dto.password}


						and t1.SALT =#{dto.salt}


						and t1.MOBILE  like concat('%',#{dto.mobile},'%')


						and t1.EMAIL =#{dto.email}


						and t1.USER_IMAGE =#{dto.userImage}


						and t1.SEX =#{dto.sex}


						and t1.BIRTHDAY =#{dto.birthday}


						and t1.NATIONALITY =#{dto.nationality}


						and t1.EDUCATION =#{dto.education}


						and t1.JOB =#{dto.job}


						and t1.HOME_ADDRESS =#{dto.homeAddress}


						and t1.HOME_ZIPCODE =#{dto.homeZipcode}


						and t1.HOME_TEL =#{dto.homeTel}


						and t1.OFFICE_TEL =#{dto.officeTel}


						and t1.OFFICE_ADDRESS =#{dto.officeAddress}


						and t1.ORDER_BY =#{dto.orderBy}


						and t1.VALIDATE_STATE =#{dto.validateState}


						and t1.IS_LOCKED =#{dto.isLocked}


						and t1.VERSION =#{dto.version}

select t1.id , t1.user_id , t1.org_id , t2.user_name , t3.org_name , t2.VALIDATE_STATE , t1.org_type , (SELECT B.POSITION_NAME FROM SYS_POSITION B WHERE B.ID=( SELECT A.POSITION_ID FROM SYS_ORG_USER A WHERE A.USER_ID=t1.user_id limit 1,1)) AS POSITION_CN
from vmuser_vmorg_map t1 left join sys_user t2 on t1.user_id=t2.id left join vmtree_info t3 on t1.org_id = t3.org_id and t1.org_type=t3.org_type where 1=1
and t1.ID =#{dto.id}
and t1.USER_ID =#{dto.userId}
and t1.ORG_ID in ( ${orgIds} )
and t1.ORG_TYPE =#{orgType}
and t2.user_name like concat('%',#{userName},'%')
and t3.org_name like concat('%',#{orgName},'%')
order by t1.id desc

    	     select 
						t1.id                             ,
						t1.user_id                        ,
						t1.org_id                         ,
						t1.org_type                       
				from vmuser_vmorg_map 		t1
				where 1=1 

						and t1.USER_ID =#{dto.userId}


						and t1.ORG_ID =#{dto.orgId}


						and t1.ORG_TYPE =#{dto.orgType}

    	     select 
						t1.id                             ,
						t1.user_id                        ,
						t1.org_id                         ,
						t1.org_type                       
				from vmuser_vmorg_map 		t1
				where 1=1 

						and t1.ID = #{dto.id}


						and t1.USER_ID = #{dto.userId}


						and t1.ORG_ID = #{dto.orgId}


						and t1.ORG_TYPE = #{dto.orgType}

mapper.insert:


	SELECT AUTO_INCREMENT FROM INFORMATION_SCHEMA.TABLES WHERE  TABLE_NAME='vmuser_vmorg_map'

insert into vmuser_vmorg_map
(   		
		id                             ,
		user_id                        ,
		org_id                         ,
		org_type                       
)
values(#{dto.id}
		,#{dto.userId,jdbcType=DECIMAL}
		,#{dto.orgId,jdbcType=DECIMAL}
		,#{dto.orgType,jdbcType=VARCHAR}
)

mapper.update:
     update vmuser_vmorg_map t1
           set 
		           t1.user_id                        =#{dto.userId,jdbcType=DECIMAL},
		           t1.org_id                         =#{dto.orgId,jdbcType=DECIMAL},
		           t1.org_type                       =#{dto.orgType,jdbcType=VARCHAR}
     where t1.id = #{dto.id}
