mapper.select:
    	 		select 
						t1.id                             ,
						t1.industry_name                  ,
						t1.industry_type                  ,
						t1.PARENT_CODE                      ,
						t1.validate_state                 

				from sys_industry 	t1
				where 1=1
				and t1.validate_state='1'

						and t1.ID =#{dto.id}


						and t1.INDUSTRY_NAME =#{dto.industryName}


						and t1.INDUSTRY_TYPE =#{dto.industryType}


						and t1.PARENT_CODE =#{dto.parentCode}


						and t1.VALIDATE_STATE =#{dto.validateState}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.industry_name                  ,
						t1.industry_type                  ,
						t1.PARENT_CODE                      ,
						t1.validate_state,
						t1.industry_code                 
				from sys_industry 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.INDUSTRY_NAME = #{dto.industryName}


						and t1.INDUSTRY_TYPE = #{dto.industryType}


						and t1.PARENT_CODE = #{dto.parentCode}


						and t1.VALIDATE_STATE = #{dto.validateState}

    	     select 
						t1.id      ID                       ,
						t1.industry_name INDUSTRYNAME                 ,
						t1.industry_type  INDUSTRYTYPE                ,
						t1.PARENT_CODE      PARENTCODE              ,
						t1.industry_code   INDUSTRYCODE               
				from sys_industry 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.INDUSTRY_NAME = #{dto.industryName}


						and t1.INDUSTRY_TYPE = #{dto.industryType}


						and t1.PARENT_CODE = #{dto.parentCode}


						and t1.VALIDATE_STATE = #{dto.validateState}

mapper.insert:


	(SELECT auto_increment FROM information_schema.TABLES WHERE  TABLE_NAME='sys_industry')

insert into sys_industry
(   		
		id                             ,
		industry_name                  ,
		industry_type                  ,
		PARENT_CODE                      ,
		validate_state                 
)
values(#{dto.id}
		,#{dto.industryName,jdbcType=VARCHAR}
		,#{dto.industryType,jdbcType=VARCHAR}
		,#{dto.parentCode,jdbcType=DECIMAL}
		,'1'
)
