mapper.select:
select t1.id , t1.app_id , t1.template_no , t1.template_name , t1.template_type , t1.validate_state , t1.version
from sys_template t1 where 1=1 and t1.validate_state='1'
and t1.ID =#{dto.id}
and t1.APP_ID like concat('%',#{dto.appId},'%')
and t1.TEMPLATE_NO like concat('%',#{dto.templateNo},'%')
and t1.TEMPLATE_NAME like concat('%',#{dto.templateName},'%')
and t1.TEMPLATE_TYPE =#{dto.templateType}
and t1.VALIDATE_STATE =#{dto.validateState}
and t1.VERSION =#{dto.version}
order by t1.id desc

    	     select 
						t1.id                             ,
						t1.app_id                         ,
						t1.template_no                    ,
						t1.template_name                  ,
						t1.template_type                  ,
						t1.template_content               ,
						t1.validate_state                 ,
						t1.version                        
				from sys_template 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.APP_ID = #{dto.appId}


						and t1.TEMPLATE_NO = #{dto.templateNo}


						and t1.TEMPLATE_NAME = #{dto.templateName}


						and t1.TEMPLATE_TYPE = #{dto.templateType}


						and t1.TEMPLATE_CONTENT = #{dto.templateContent}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}

mapper.insert:


	SELECT auto_increment FROM information_schema.TABLES WHERE  TABLE_NAME='sys_template'

insert into sys_template
(   		
		id                             ,
		app_id                         ,
		template_no                    ,
		template_name                  ,
		template_type                  ,
		template_content               ,
		validate_state                 ,
		version                        
)
values(#{dto.id}
		,#{dto.appId,jdbcType=VARCHAR}
		,#{dto.templateNo,jdbcType=VARCHAR}
		,#{dto.templateName,jdbcType=VARCHAR}
		,#{dto.templateType,jdbcType=VARCHAR}
		,#{dto.templateContent,typeHandler=org.apache.ibatis.type.BlobTypeHandler}
		,'1'
		,#{dto.version,jdbcType=DECIMAL}
)

mapper.update:
	     update sys_template t1
          		set  t1.template_content =#{templateContent}
	     where t1.id = #{id}
