mapper.select:
select a.source_type sourceType,1 type,count(a.id) c from videos a where 1 = 1 and a.is_deleted = 0 and a.media_state = 2
and a.created_at >= #{startTime,jdbcType=VARCHAR}
and a.created_at <= #{endTime,jdbcType=VARCHAR}
group by a.source_type
union all select a.source_type sourceType,2 type,count(a.id) c from videos a inner join user_policies b on a.user_id = b.uid where 1 = 1 and a.is_deleted = 0 and a.media_state = 2
and a.created_at >= #{startTime,jdbcType=VARCHAR}
and a.created_at <= #{endTime,jdbcType=VARCHAR}
and exists(select user_id from user_external_maps tt where tt.user_id != a.user_id) and b.user_type not in('iss','ims') and a.user_id not in(1, 8, 18, 864, 1077, 847) group by a.source_type
union all select a.source_type sourceType,3 type,count(a.id) c from videos a where 1 = 1 and a.is_deleted = 0 and a.media_state = 2 and a.user_id in (select tt.user_id from user_external_maps tt)
and a.created_at >= #{startTime,jdbcType=VARCHAR}
and a.created_at <= #{endTime,jdbcType=VARCHAR}
group by a.source_type
union all select a.source_type sourceType,4 type,count(a.id) c from videos a inner join user_policies b on a.user_id = b.uid where 1 = 1 and a.is_deleted = 0 and a.media_state = 2 and b.user_type in('weibo_zhengwu','weibo_newsmedia')
and a.created_at >= #{startTime,jdbcType=VARCHAR}
and a.created_at <= #{endTime,jdbcType=VARCHAR}
group by a.source_type

select b.source_type sourceType,1 type, count(a.id) c from user_video_share_logs a left join videos b on a.vid = b.id where 1 = 1 and a.state = 2
and a.shared_time >= #{startTime,jdbcType=VARCHAR}
and a.shared_time <= #{endTime,jdbcType=VARCHAR}
group by b.source_type
union all select b.source_type sourceType,2 type, count(a.id) c from user_video_share_logs a inner join videos b on a.vid = b.id inner join user_policies c on a.user_id = c.uid where 1=1 and a.state = 2
and a.shared_time >= #{startTime,jdbcType=VARCHAR}
and a.shared_time <= #{endTime,jdbcType=VARCHAR}
and a.user_id not in (1,8,18,864,1077,847) and c.user_type not in ('iss','ims') group by b.source_type
union all select b.source_type sourceType,3 type, count(a.id) c from user_video_share_logs a inner join videos b on a.vid = b.id where 1=1 and a.state = 2
and a.shared_time >= #{startTime,jdbcType=VARCHAR}
and a.shared_time <= #{endTime,jdbcType=VARCHAR}
and exists(select tt.id from user_external_maps tt where tt.user_id=a.user_id) group by b.source_type
union all select b.source_type sourceType,4 type, count(a.id) c from user_video_share_logs a inner join videos b on a.vid = b.id inner join user_policies c on a.user_id = c.uid where 1=1 and a.state = 2
and a.shared_time >= #{startTime,jdbcType=VARCHAR}
and a.shared_time <= #{endTime,jdbcType=VARCHAR}
and c.user_type in('weibo_zhengwu','weibo_newsmedia') group by b.source_type

select count(a.id) from videos a 

	inner join user_policies b on a.user_id = b.uid

where 1=1
and a.is_deleted = 0

	and a.source_type = #{sourceTyp,jdbcType=INTEGER}


	and a.source_type in(${sourceTypes})


	and a.created_at >= #{startCreatedTime,jdbcType=VARCHAR}


	and a.created_at <= #{endCreatedTime,jdbcType=VARCHAR}




		and b.user_type = #{userType,jdbcType=VARCHAR}


		and b.user_type in(${userTypes})


		and b.user_type not in(${notUserTypes})

select count(a.id) from user_video_share_logs a
inner join videos b on a.vid = b.id

	inner join user_policies c on a.user_id = c.uid

where 1=1
and a.state = #{state,jdbcType=INTEGER}

	and a.shared_time >= #{startShareTime,jdbcType=VARCHAR}


	and a.shared_time <= #{endShareTime,jdbcType=VARCHAR}


	and b.source_type = #{sourceTyp,jdbcType=INTEGER}


	and b.source_type in(${sourceTypes})



		and c.user_type = #{userType,jdbcType=VARCHAR}


		and c.user_type in(${userTypes})


		and c.user_type not in(${notUserTypes})

select a.source_type * 1 sourceType,date_format(a.created_at,'%Y-%m-%d') d,count(*) c
from mam_materials a 
where a.is_deleted = #{isDeleted,jdbcType=INTEGER}
and media_state = #{mediaState,jdbcType=INTEGER} 
and a.created_at between #{startTime,jdbcType=VARCHAR} and #{endTime,jdbcType=VARCHAR}

group by a.source_type,date_format(a.created_at,'%Y-%m-%d')

select t.d,t.sourceType,count(t.user_id) c from (
	select a.user_id,a.source_type * 1 sourceType,date_format(a.created_at,'%Y-%m-%d') d,count(*) c
	from mam_materials a 
	where a.is_deleted = #{isDeleted,jdbcType=INTEGER}
	and media_state = #{mediaState,jdbcType=INTEGER} 
	and a.created_at between #{startTime,jdbcType=VARCHAR} and #{endTime,jdbcType=VARCHAR}

	group by a.user_id,a.source_type,date_format(a.created_at,'%Y-%m-%d')
) t 
group by t.d,t.sourceType
order by t.d desc

select sum(a.vv) sumvv from weibo_statistics a 
where 1=1 

	and a.updated_at >= #{startTime,jdbcType=VARCHAR}


	and a.updated_at <= #{endTime,jdbcType=VARCHAR}

select date_format(a.created_at,'%Y-%m-%d') d,a.source_type sourceType,count(a.id) 
from videos a 
where 1 = 1
and a.is_deleted = 0

	and a.media_state = #{mediaState,jdbcType=INTEGER}	


	and a.created_at >= #{startTime,jdbcType=VARCHAR}


	and a.created_at <= #{endTime,jdbcType=VARCHAR}

 group by a.source_type,date_format(a.created_at,'%Y-%m-%d')
