mapper.select:
SELECT SUBSTR(T1.CREATE_TIME,9,2) CTIME, T1.LOG_LEVEL, COUNT(T1.APP_FLAG) AS COUNT, T1.APP_FLAG FROM SYS_APP_ERROR_INFO T1 WHERE 1 = 1
AND T1.NODE_NAME = #{dto.nodeName}
AND T1.APP_FLAG = #{dto.appFlag}
AND T1.CREATE_TIME >= #{dto.ctime} AND T1.CREATE_TIME < date_format(date_add(CONCAT(#{dto.ctime},'-01'),INTERVAL 1 month),'%Y-%m')
AND T1.LOG_LEVEL = #{dto.logLevel}
GROUP BY T1.APP_FLAG,SUBSTR(T1.CREATE_TIME,9,2),T1.LOG_LEVEL

SELECT SUBSTR(T1.CREATE_TIME,12,2) CTIME, T1.LOG_LEVEL, COUNT(T1.APP_FLAG) AS COUNT, T1.APP_FLAG FROM SYS_APP_ERROR_INFO T1 WHERE 1 = 1
AND T1.NODE_NAME = #{dto.nodeName}
AND T1.APP_FLAG = #{dto.appFlag}
AND T1.CREATE_TIME >= #{dto.ctime} AND T1.CREATE_TIME < date_format(date_add(#{dto.ctime},INTERVAL 1 day),'%Y-%m-%d')
AND T1.LOG_LEVEL = #{dto.logLevel}
GROUP BY T1.APP_FLAG,SUBSTR(T1.CREATE_TIME,12,2),T1.LOG_LEVEL

SELECT
T1.LOG_LEVEL, COUNT(T1.APP_FLAG) AS COUNT, T1.APP_FLAG FROM SYS_APP_ERROR_INFO T1 WHERE 1 = 1
AND T1.NODE_NAME = #{dto.nodeName}
AND T1.APP_FLAG = #{dto.appFlag}
AND T1.CREATE_TIME >= #{dto.ctime} AND T1.CREATE_TIME < date_format(date_add(CONCAT(#{dto.ctime},'-01'),INTERVAL 1 month),'%Y-%m')
AND T1.LOG_LEVEL = #{dto.logLevel}
GROUP BY T1.APP_FLAG,T1.LOG_LEVEL

SELECT
T1.LOG_LEVEL, COUNT(T1.APP_FLAG) AS COUNT, T1.APP_FLAG FROM SYS_APP_ERROR_INFO T1 WHERE 1 = 1
AND T1.NODE_NAME = #{dto.nodeName}
AND T1.APP_FLAG = #{dto.appFlag}
AND T1.CREATE_TIME >= #{dto.ctime} AND T1.CREATE_TIME < date_format(date_add(#{dto.ctime},INTERVAL 1 day),'%Y-%m-%d')
AND T1.LOG_LEVEL = #{dto.logLevel}
GROUP BY T1.APP_FLAG,T1.LOG_LEVEL

    	 		select 
						t1.id                             ,
						t1.node_name                      ,
						t1.app_flag                       ,
						t1.create_time                    ,
						t1.concent                        ,
						t1.FILE_NAME                      ,
						t1.LEVEL_SETUP_ID                      ,
						t1.log_level      


				from sys_app_error_info 	t1
				where 1=1

						and t1.ID =#{dto.id}


						and t1.NODE_NAME =#{dto.nodeName}


						and t1.APP_FLAG =#{dto.appFlag}


						and t1.CREATE_TIME =#{dto.createTime}


						and  t1.CREATE_TIME >= #{startTime} 


						and  t1.CREATE_TIME < date_format(date_add(#{endTime},INTERVAL 1 day),'%Y-%m-%d') 


						and t1.CONCENT like concat('%',#{dto.concent},'%')


						and t1.LOG_LEVEL =#{dto.logLevel}


				order by t1.id desc

select t1.id , t1.node_name , t1.app_flag , t1.create_time , t1.concent , t1.LEVEL_SETUP_ID , t1.FILE_NAME from sys_app_error_info t1 where 1=1
and t1.ID = #{dto.id}
and t1.ID = ( select max(id) id from SYS_APP_ERROR_INFO t where t.node_name=#{dto.nodeName} and t.FILE_NAME=#{dto.fileName} and t.app_flag=#{dto.appFlag})
and t1.CREATE_TIME = #{dto.createTime}
and t1.CONCENT = #{dto.concent}

mapper.insert:
begin

			insert into sys_app_error_info
			(   		
					node_name                      ,
					app_flag                       ,
					create_time                    ,
					concent                        ,
					FILE_NAME      
			)
			select  #{dto.nodeName,jdbcType=VARCHAR}
					,#{dto.appFlag,jdbcType=VARCHAR}
					,#{dto.createTime,jdbcType=VARCHAR}
					,#{dto.concent,jdbcType=VARCHAR}
					,#{dto.fileName,jdbcType=VARCHAR};

end;

mapper.update:
     update sys_app_error_info t1
     set 
          t1.LOG_LEVEL                      =#{level,jdbcType=VARCHAR}
		 ,t1.LEVEL_SETUP_ID                 =#{levelSetupId,jdbcType=VARCHAR}
     where t1.MATCHED_FLAG='N'
     and t1.concent like concat('%',#{keyWord,jdbcType=VARCHAR},'%')

     and t1.APP_FLAG = #{appFlag}
