mapper.select:
select t1.id , t1.app_flag , t1.email , t1.tel , t1.log_level , t1.manage_name , t1.KEY_WORD
from sys_app_manage_contact_way t1 where 1=1
and t1.ID =#{dto.id}
and t1.APP_FLAG =#{dto.appFlag}
and t1.EMAIL =#{dto.email}
and t1.TEL =#{dto.tel}
and t1.MANAGE_NAME like '%'||#{dto.manageName}||'%'
and t1.LOG_LEVEL =#{dto.logLevel}
and t1.KEY_WORD like '%'||#{dto.keyWord}||'%'
order by t1.id desc

select t1.id , t1.app_flag , t1.email , t1.tel , t1.log_level , t1.manage_name , t1.KEY_WORD
from sys_app_manage_contact_way t1 where 1=1
and t1.ID = #{dto.id}
and t1.APP_FLAG = #{dto.appFlag}
and t1.EMAIL = #{dto.email}
and t1.TEL = #{dto.tel}
and t1.MANAGE_NAME like '%'||#{dto.manageName}||'%'
and t1.LOG_LEVEL =#{dto.logLevel}
and t1.KEY_WORD like '%'||#{dto.keyWord}||'%'

mapper.insert:

	select seq_SYS_APP_MANAGE_CONTACT_WAY.nextval as id from dual

insert into sys_app_manage_contact_way
(   		
		id                             ,
		app_flag                       ,
		email                          ,
		tel                            ,
		log_level                      ,
		manage_name                    ,
		KEY_WORD  
)
values(#{dto.id}
		,#{dto.appFlag,jdbcType=VARCHAR}
		,#{dto.email,jdbcType=VARCHAR}
		,#{dto.tel,jdbcType=VARCHAR}
		,#{dto.logLevel,jdbcType=VARCHAR}
		,#{dto.manageName,jdbcType=VARCHAR}
		,#{dto.keyWord,jdbcType=VARCHAR}
)
