mapper.select:
    	 		select 
						t1.id                             ,
						t1.resoure_name                   ,
						t1.resoure_type                   ,
						t1.resoure_url                    ,
						t1.permission                     ,
						t1.parent_id                      ,
						t1.parent_ids                     ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        

				from sys_resource 	t1
				where 1=1
				and t1.validate_state='1'

						and t1.ID =#{dto.id}


						and t1.RESOURE_NAME =#{dto.resoureName}


						and t1.RESOURE_TYPE =#{dto.resoureType}


						and t1.RESOURE_URL =#{dto.resoureUrl}


						and t1.PERMISSION =#{dto.permission}


						and t1.PARENT_ID =#{dto.parentId}


						and t1.PARENT_IDS =#{dto.parentIds}


						and t1.APP_ID =#{dto.appId}


						and t1.VALIDATE_STATE =#{dto.validateState}


						and t1.VERSION =#{dto.version}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.resoure_name                   ,
						t1.resoure_type                   ,
						t1.resoure_url                    ,
						t1.permission                     ,
						t1.parent_id                      ,
						t1.parent_ids                     ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        
				from sys_resource 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.RESOURE_NAME = #{dto.resoureName}


						and t1.RESOURE_TYPE = #{dto.resoureType}


						and t1.RESOURE_URL = #{dto.resoureUrl}


						and t1.PERMISSION = #{dto.permission}


						and t1.PARENT_ID = #{dto.parentId}


						and t1.PARENT_IDS = #{dto.parentIds}


						and t1.APP_ID = #{dto.appId}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}

mapper.insert:

	select seq_SYS_RESOURCE.nextval as id from dual

insert into sys_resource
(   		
		id                             ,
		resoure_name                   ,
		resoure_type                   ,
		resoure_url                    ,
		permission                     ,
		parent_id                      ,
		parent_ids                     ,
		app_id                         ,
		validate_state                 ,
		version                        
)
values(#{dto.id}
		,#{dto.resoureName,jdbcType=VARCHAR}
		,#{dto.resoureType,jdbcType=VARCHAR}
		,#{dto.resoureUrl,jdbcType=VARCHAR}
		,#{dto.permission,jdbcType=VARCHAR}
		,#{dto.parentId,jdbcType=VARCHAR}
		,#{dto.parentIds,jdbcType=VARCHAR}
		,#{dto.appId,jdbcType=DECIMAL}
		,'1'
		,#{dto.version,jdbcType=DECIMAL}
)

mapper.select:

select t4.* from (select t1.role_id,t1.resoure_id,t2.role_code,t3.resoure_url,t3.permission,t3.resoure_type from sys_acl t1 left join sys_role t2  on t1.role_id=t2.id  left join sys_resource t3 on t1.resoure_id=t3.id) t4 where 1=1 

					and t4.resoure_type = #{resoureType}


					and t4.role_code = #{roleCode}
