mapper.select:
    	 		select 
						t1.id                             ,
						t1.role_id                        ,
						t1.role_group_id                  ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        

				from sys_role_group_role 	t1
				where 1=1
				and t1.validate_state='1'

						and t1.ID =#{dto.id}


						and t1.ROLE_ID =#{dto.roleId}


						and t1.ROLE_GROUP_ID =#{dto.roleGroupId}


						and t1.APP_ID =#{dto.appId}


						and t1.VALIDATE_STATE =#{dto.validateState}


						and t1.VERSION =#{dto.version}


				order by t1.id desc

    	     select 
						t1.id                             ,
						t1.role_id                        ,
						t1.role_group_id                  ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        
				from sys_role_group_role 		t1
				where 1=1

						and t1.ID = #{dto.id}


						and t1.ROLE_ID = #{dto.roleId}


						and t1.ROLE_GROUP_ID = #{dto.roleGroupId}


						and t1.APP_ID = #{dto.appId}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}

mapper.insert:

	select seq_sys_role_group_role.nextval as id from dual

insert into sys_role_group_role
(   		
		id                             ,
		role_id                        ,
		role_group_id                  ,
		app_id                         ,
		validate_state                 ,
		version                        
)
values(#{dto.id}
		,#{dto.roleId,jdbcType=DECIMAL}
		,#{dto.roleGroupId,jdbcType=DECIMAL}
		,#{dto.appId,jdbcType=DECIMAL}
		,'1'
		,#{dto.version,jdbcType=DECIMAL}
)
