mapper.select:
select t1.id , t1.role_name , t1.role_code , t1.role_type , t1.app_id , t1.validate_state , t1.version
from sys_role t1
left join sys_role_group_role t2 on t1.id = t2.role_id
where 1=1 and t1.validate_state='1'
and t2.role_group_id in (${groupIds})
and t1.ID =#{dto.id}
and t1.role_name like '%'||#{dto.roleName}||'%'
and t1.ROLE_CODE like '%'||#{dto.roleCode}||'%'
and t1.ROLE_TYPE =#{dto.roleType}
and t1.APP_ID =#{dto.appId}
and t1.VALIDATE_STATE =#{dto.validateState}
and t1.VERSION =#{dto.version}
and t1.id in (select a.role_id from sys_role_user a where a.target_id in (select id from sys_org a where a.id=#{orgId} or a.parent_ids like '%/'||#{orgId}||'/%') and a.target_type='org')
order by t1.id desc

    	     select 
						t1.id                             ,
						t1.role_name                      ,
						t1.role_code                      ,
						t1.role_type                      ,
						t1.app_id                         ,
						t1.validate_state                 ,
						t1.version                        
				from sys_role 		t1

						left join sys_role_group_role t2 on t1.id = t2.role_id

				where 1=1


						and t2.role_group_id in (${groupIds})


						and t1.ID = #{dto.id}


						and t1.ROLE_NAME = #{dto.roleName}


						and t1.ROLE_CODE = #{dto.roleCode}


						and t1.ROLE_TYPE = #{dto.roleType}


						and t1.APP_ID = #{dto.appId}


						and t1.VALIDATE_STATE = #{dto.validateState}


						and t1.VERSION = #{dto.version}

mapper.insert:

	select seq_SYS_ROLE.nextval as id from dual

insert into sys_role
(   		
		id                             ,
		role_name                      ,
		role_code                      ,
		role_type                      ,
		app_id                         ,
		validate_state                 ,
		version                        
)
values(#{dto.id}
		,#{dto.roleName,jdbcType=VARCHAR}
		,#{dto.roleCode,jdbcType=VARCHAR}
		,#{dto.roleType,jdbcType=VARCHAR}
		,#{dto.appId,jdbcType=DECIMAL}
		,'1'
		,#{dto.version,jdbcType=DECIMAL}
)
